sqlMap.procedure:
BEGIN INSERT INTO ISSUED_SUPPLIES_GROUP2(issued_id, supply_id, issue_date, requestor, quantity,dept_id,last_user,last_update)
VALUES(issuedSequence.NEXTVAL,#supplyId#, TO_DATE(TO_DATE(#issueDate#, 'yyyy-mm-dd'), 'dd-MON-yyyy'),#requestor#, #quantity#,#departmentId#,#lastUser#, TO_DATE(#lastUpdate#, 'dd-mm-yy'));
UPDATE supplies_group2 SET actual_count = actual_count-#quantity# WHERE supply_id = #supplyId#; END;

BEGIN	
	UPDATE ISSUED_SUPPLIES_GROUP2  SET 
	supply_id = #supplyId#, issue_date = TO_DATE(TO_DATE(#issueDate#, 'yyyy-mm-dd'), 'dd-MON-yyyy'), requestor = #requestor#,
	quantity = #quantity#,dept_id = #departmentId#,last_user = #lastUser#,last_update = TO_DATE(#lastUpdate#, 'dd-mm-yy')	
	WHERE supply_id = #supplyId#;		
END;
